#include <stdio.h>
#include "ceuler.h"

int main()
{
    char bignum[1000]="";

    printf("Project Euler - Problem 8:\n"
           "Find the thirteen adjacent digits in the 1000-digit number that have the greatest product.\n"
           "What is the value of this product?\n\n%s\n\n", bignum);

    // Begin time tracking
    struct timeval start;
    gettimeofday(&start, NULL);

    unsigned long long largest = 0, candidate = 1;
    const int adjacentSize = 13;

    for (int numIndex = 0 ; numIndex <= (1000-adjacentSize) ; numIndex++)
    {
        candidate = 1;
        for (int adjacentIndex = 0 ; adjacentIndex < adjacentSize ; adjacentIndex++)
        {
            candidate = candidate * (bignum[numIndex + adjacentIndex] - '0');
        }
        if (candidate > largest)
            largest = candidate;
    }

    printf("Greatest product: %llu\n", largest);
    printElapsedTime(start);

    return 0;
}
